-- ============================================================
-- 4. CLASS SESSIONS — via generate_series (2026-02-02 to 2026-03-08)
-- ============================================================
-- Auto-generate sessions for each group matching its day_of_week
-- ~3% cancelled for realism (deterministic via hashtext)

INSERT INTO class_sessions (group_id, session_date, instructor_id, is_cancelled)
SELECT
  g.id,
  d::date,
  g.instructor_id,
  -- ~3% cancelled: hash the group+date and check mod
  (abs(hashtext(g.id::text || d::text)) % 33 = 0)
FROM groups g
CROSS JOIN generate_series('2026-02-02'::date, '2026-03-08'::date, '1 day'::interval) AS d
WHERE EXTRACT(DOW FROM d) = g.day_of_week
ON CONFLICT (group_id, session_date) DO NOTHING;

-- ============================================================
-- 5. ATTENDANCE — for past/current sessions only (≤ 2026-02-21)
-- ============================================================
-- Deterministic pseudo-random status via hashtext:
--   ~70% present, ~15% absent, ~15% excused

INSERT INTO attendance (session_id, student_id, status)
SELECT
  cs.id,
  gm.student_id,
  CASE
    WHEN abs(hashtext(cs.id::text || gm.student_id::text)) % 20 < 14 THEN 'present'::attendance_status
    WHEN abs(hashtext(cs.id::text || gm.student_id::text)) % 20 < 17 THEN 'absent'::attendance_status
    ELSE 'excused'::attendance_status
  END
FROM class_sessions cs
JOIN group_memberships gm ON gm.group_id = cs.group_id AND gm.is_active = true
WHERE cs.session_date <= '2026-02-21'
  AND cs.is_cancelled = false
ON CONFLICT (session_id, student_id) DO NOTHING;

